import java.util.Scanner;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc= new Scanner(System.in);
			 int n = sc.nextInt();
			 int num=sc.nextInt();
		for(int i=1;i<=n;i++){
		  System.out.println(num+"*"+i+""+"="+num*i);
		}
		}

	}


